mapper.select:
/* 게시글 리스트 조회 - /src/main/resources/mappers/postMapper.xml - selectPostList */ SELECT all_list.* FROM ( SELECT a.post_no , a.title , a.frst_regt_no , b.login_id , b.nickname , a.frst_regt_dt , a.last_updt_dt , a.view_cnt , a.thumbnail , CASE WHEN count(c.file_no) > 0 THEN 'Y' ELSE 'N' END AS file_yn FROM tb_post AS a LEFT JOIN tb_user AS b ON a.frst_regt_no = b.user_no LEFT JOIN tb_file AS c ON a.post_no = c.post_no AND c.del_yn = 'N'
WHERE 1=1 AND a.del_yn = 'N'
GROUP BY post_no ORDER BY post_no DESC ) all_list LIMIT #{start}, #{pageSize}

mapper.update:
	/* 게시글 수정 - /src/main/resources/mappers/postMapper.xml - modifyPost */
		UPDATE
			tb_post
    	SET
    	  title			= #{title}
    	, content		= #{content}
    	, last_updt_no	= #{lastUpdtNo}
    	, last_updt_dt	= NOW()
    	WHERE
    		post_no 	= 	#{postNo}

mapper.insert:
/* 게시글 작성- /src/main/resources/mappers/postMapper.xml - insertPostInfo */
    INSERT INTO tb_post
    (
    	  title
    	, content
    	, frst_regt_no
    	, frst_regt_dt
    	, last_updt_no
    	, last_updt_dt
    	, view_cnt
    	, latitude
    	, longitude
    	, del_yn
    	, board_no
    	, thumbnail
    )
    VALUES
    (
    	  #{title}
    	, #{content}
    	, #{frstRegtNo}
    	, NOW()
    	, #{frstRegtNo}
    	, NOW()
    	, 0
    	, #{latitude}
		, #{longitude}
		, 'N'
		, #{boardNo}
		, #{thumbnail}
    )

	/* 첨부파일 저장 - /src/main/resources/mappers/postMapper.xml - insertFile */
    	INSERT INTO tb_file
    	(
    		  post_no
    		, file_sn
    		, original_file_nm
    		, stored_file_nm
    		, file_size
    		, stored_path
    		, file_cd
    		, file_ext
    		, frst_regt_no
    		, frst_regt_dt
    		, del_yn
    	)
    	VALUES
    	(
    		  #{postNo}
    		, #{fileSn}
    		, #{originalFileNm}
    		, #{storedFileNm}
    		, #{fileSize}
    		, #{storedPath}
    		, #{fileCd}
    		, #{fileExt}
    		, #{frstRegtNo}
    		, NOW()
    		, 'N'
    	)

mapper.update:
	/* 게시글 첨부파일 삭제 - /src/main/resources/mappers/postMapper.xml - deleteFiles */
    	UPDATE tb_file
    	SET
    		  del_yn = 'Y'
    	WHERE 1=1
    	AND post_no = #{postNo}

	/* 선택한 첨부파일 삭제 - /src/main/resources/mappers/postMapper.xml - deleteFile */
    	UPDATE tb_file
    	SET
			del_yn = 'Y'
    	WHERE 1=1
    	AND file_no = #{fileNo}
